import React, { useEffect, useState } from 'react';
import firebase from '../util/firebase'
import SensorPlot from '../component/SensorPlot'
import { Row, Col, Container, InputGroup, DropdownButton, Dropdown, FormControl } from 'react-bootstrap';
import Form from 'react-bootstrap/Form'
import DatePicker from 'react-date-picker';

function Graph(props) {

    const [sensorDate, setSensorDate] = useState()
    const [sensorTime, setSensorTime] = useState()
    const [sensorPH, setSensorPH] = useState()
    const [sensorTDS, setSensorTDS] = useState()
    const [sensorDO, setSensorDO] = useState()
    const [sensorEC, setSensorEC] = useState()
    const [sensorORP, setSensorORP] = useState()
    const [sensorTEMP, setSensorTEMP] = useState()
    const [sensorLon, setSensorLon] = useState()
    const [sensorLat, setSensorLat] = useState()
    const [sensorWC, setSensorWC] = useState()
    const [dbList, setDbList] = useState()
    const [idMachine, setIdMachine] = useState('kapal-1')

    const [graphDate, setGraphDate] = useState(new Date());
    const [graphSelect, setGraphSelect] = useState(0);
    const handleGraphSelect = (e) => {
        setGraphSelect(e)
    }
    const monthText = ["Januari", "Februari", "Maret", "April", "Mei", "Juni", "Juli", "Agustus", "September", "Oktober", "November", "Desember"];
    const handleDate = (e) => {
        console.log(e);
        setGraphDate(e.target.value)
        //getDate
        //getMonth
        //getFullYear
    }

    useEffect(() => {



        const dbRef = firebase.database().ref('dbrt').child(idMachine).child('2021').child(1).child(4)
        dbRef.on('value', (snapshot) => {
            const db = snapshot.val()
            const listDate = []
            const listTime = []
            const listPH = []
            const listTDS = []
            const listDO = []
            const listEC = []
            const listORP = []
            const listTEMP = []
            const listLon = []
            const listLat = []
            const listDb = []
            const listWC = []
            for (let id in db) {
                listDate.push(db[id].date)
                listTime.push(db[id].time)
                listPH.push(db[id].ph)
                listTDS.push(db[id].tds)
                listDO.push(db[id].do)
                listEC.push(db[id].ec)
                listORP.push(db[id].orp)
                listTEMP.push(db[id].temp)
                listLon.push(db[id].lon)
                listLat.push(db[id].lat)
                listDb.push(db[id])
                listWC.push(db[id].wc)
            }
            setSensorDate(listDate)
            setSensorTime(listTime)
            setSensorPH(listPH)
            setSensorTDS(listTDS)
            setSensorDO(listDO)
            setSensorEC(listEC)
            setSensorORP(listORP)
            setSensorTEMP(listTEMP)
            setSensorLon(listLon)
            setSensorLat(listLat)
            setSensorWC(listWC)
            setDbList(listDb)
        })
    },[])

    return (
        <div>
            <Container fluid="md">

                <Row xs={8} md={3} className="justify-content-md-center">
                    <div className="Container" style={{ textAlign: "center" }}>
                        {graphSelect>0?<h1 className="center">Grafik Bulanan</h1>:<h1 className="center">Grafik Harian</h1>}
                        {graphSelect>0
                        ?<h1 className="center">{`${monthText[graphDate.getMonth()]} ${graphDate.getFullYear()}`}</h1>
                        :<h1 className="center">{`${graphDate.getDate()} ${monthText[graphDate.getMonth()]} ${graphDate.getFullYear()}`}</h1>}
                    </div>
                    <InputGroup className="mb-3">
                        <DropdownButton
                            variant="secondary"
                            title={graphSelect > 0 ? "Bulanan" : "Harian"}
                            id="input-group-dropdown-1"
                            onSelect={handleGraphSelect}
                        >
                            <Dropdown.Item eventKey="0">Harian</Dropdown.Item>
                            <Dropdown.Item eventKey="1">Bulanan</Dropdown.Item>
                        </DropdownButton>

                        <DatePicker
                            onChange={handleDate}
                            value={graphDate}
                            format="d-M-y"
                        />
                    </InputGroup>
                </Row>

                <Row xs={1} md={1} className="g-4 justify-content-md-center">
                    <SensorPlot
                        labelname="PH Meter"
                        data={sensorPH}
                        labels={sensorTime}
                        linecolor='200, 0, 0'
                    />
                    <SensorPlot
                        labelname="TDS"
                        data={sensorTDS}
                        labels={sensorTime}
                        linecolor='100, 76, 102'
                    />
                    <SensorPlot
                        labelname="DO"
                        data={sensorDO}
                        labels={sensorTime}
                        linecolor='3, 6, 202'
                    />
                    <SensorPlot
                        labelname="EC"
                        data={sensorEC}
                        labels={sensorTime}
                        linecolor='205, 120, 62'
                    />
                    <SensorPlot
                        labelname="ORP"
                        data={sensorORP}
                        labels={sensorTime}
                        linecolor='130, 150, 10'
                    />
                    <SensorPlot
                        labelname="Temperatur"
                        data={sensorTEMP}
                        labels={sensorTime}
                        linecolor='3, 76, 102'
                    />
                    <SensorPlot
                        labelname="Water Contact"
                        data={sensorWC}
                        labels={sensorTime}
                        linecolor='20, 153, 23'
                    />
                </Row>

            </Container>
        </div>
    );
}

export default Graph;
