import React, { useState } from 'react';
import firebase from '../util/firebase';



function Form(props) {

    const [sensorPH,    setSensorPH]   = useState('')
    const [sensorTDS,   setSensorTDS]  = useState('')
    const [sensorDO,    setSensorDO]   = useState('')
    const [sensorEC,    setSensorEC]   = useState('')
    const [sensorORC,   setSensorORC]  = useState('')
    const [sensorTEMP,  setSensorTEMP] = useState('')


    const handleOnClick = () => {
        //bagian ini untuk update
        const kirimRef = firebase.database().ref("realtime-db")
        const data  = {
            time:0,
            ph:sensorPH,
            tds:sensorTDS,
            do:sensorDO,
            ec:sensorEC,
            orc:sensorORC,
            temp:sensorTEMP,
            long:0,
            lat:0,
        }
        kirimRef.push(data)
        //=======================
    }


    return (
        <div>
            <input type="number" value={sensorPH} onChange={(e) => setSensorPH(e.target.value)} placeholder="Sensor PH"/>
            <input type="number" value={sensorTDS} onChange={(e) => setSensorTDS(e.target.value)} placeholder="Sensor TDS"/>
            <input type="number" value={sensorDO} onChange={(e) => setSensorDO(e.target.value)} placeholder="Sensor DO"/>
            <input type="number" value={sensorEC} onChange={(e) => setSensorEC(e.target.value)} placeholder="Sensor EC"/>
            <input type="number" value={sensorORC} onChange={(e) => setSensorORC(e.target.value)} placeholder="Sensor ORC"/>
            <input type="number" value={sensorTEMP} onChange={(e) => setSensorTEMP(e.target.value)} placeholder="Sensor TEMP"/>

            <button onClick={handleOnClick}>ADD</button>
        </div>
    );
}

export default Form;
