import React, { useEffect, useState } from 'react';
import firebase from '../util/firebase'
import SensorPlot from '../component/SensorPlot'
import { Row, Col } from 'react-bootstrap';

function Graph(props) {

    const [sensorDate, setSensorDate] = useState()
    const [sensorTime, setSensorTime] = useState()
    const [sensorPH, setSensorPH] = useState()
    const [sensorTDS, setSensorTDS] = useState()
    const [sensorDO, setSensorDO] = useState()
    const [sensorEC, setSensorEC] = useState()
    const [sensorORP, setSensorORP] = useState()
    const [sensorTEMP, setSensorTEMP] = useState()
    const [sensorLon, setSensorLon] = useState()
    const [sensorLat, setSensorLat] = useState()
    const [sensorWC, setSensorWC] = useState()
    const [dbList, setDbList] = useState()

    useEffect(() => {
        const dbRef = firebase.database().ref('realtime-db/2021/09/08')
        dbRef.on('value', (snapshot) => {
            const db = snapshot.val()
            const listDate = []
            const listTime = []
            const listPH = []
            const listTDS = []
            const listDO = []
            const listEC = []
            const listORP = []
            const listTEMP = []
            const listLon = []
            const listLat = []
            const listDb = []
            const listWC = []
            for (let id in db) {
                listDate.push(db[id].date)
                listTime.push(db[id].time)
                listPH.push(db[id].ph)
                listTDS.push(db[id].tds)
                listDO.push(db[id].do)
                listEC.push(db[id].ec)
                listORP.push(db[id].orp)
                listTEMP.push(db[id].temp)
                listLon.push(db[id].lon)
                listLat.push(db[id].lat)
                listDb.push(db[id])
                listWC.push(db[id].wc)
            }
            setSensorDate(listDate)
            setSensorTime(listTime)
            setSensorPH(listPH)
            setSensorTDS(listTDS)
            setSensorDO(listDO)
            setSensorEC(listEC)
            setSensorORP(listORP)
            setSensorTEMP(listTEMP)
            setSensorLon(listLon)
            setSensorLat(listLat)
            setSensorWC(listWC)
            setDbList(listDb)
        })
    }, [])

    return (
        <div>
            <div className="p-5">
                <div className="container">
                    <Row>
                        <Col>

                        </Col>

                    </Row>



                    <SensorPlot
                        labelname="PH Meter"
                        data={sensorPH}
                        labels={sensorTime}
                        linecolor='200, 0, 0'
                    />
                    <SensorPlot
                        labelname="TDS"
                        data={sensorTDS}
                        labels={sensorTime}
                        linecolor='100, 76, 102'
                    />
                    <SensorPlot
                        labelname="DO"
                        data={sensorDO}
                        labels={sensorTime}
                        linecolor='3, 6, 202'
                    />
                    <SensorPlot
                        labelname="EC"
                        data={sensorEC}
                        labels={sensorTime}
                        linecolor='205, 120, 62'
                    />
                    <SensorPlot
                        labelname="ORP"
                        data={sensorORP}
                        labels={sensorTime}
                        linecolor='130, 150, 10'
                    />
                    <SensorPlot
                        labelname="Temperatur"
                        data={sensorTEMP}
                        labels={sensorTime}
                        linecolor='3, 76, 102'
                    />
                    <SensorPlot
                        labelname="Water Contact"
                        data={sensorWC}
                        labels={sensorTime}
                        linecolor='80, 73, 108'
                    />

                </div>
            </div>
        </div>
    );
}

export default Graph;
