import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import firebase from '../util/firebase'

function GraphPlot(props) {

    const [sensorDate,  setSensorDate] = useState()
    const [sensorTime,  setSensorTime] = useState()
    const [sensorPH,    setSensorPH]   = useState()
    const [sensorTDS,   setSensorTDS]  = useState()
    const [sensorDO,    setSensorDO]   = useState()
    const [sensorEC,    setSensorEC]   = useState()
    const [sensorORC,   setSensorORC]  = useState()
    const [sensorTEMP,  setSensorTEMP] = useState()
    const [sensorLon,   setSensorLon]  = useState()
    const [sensorLat,   setSensorLat]  = useState()
    const [dbList,      setDbList]     = useState()

    useEffect(() => {
        const dbRef = firebase.database().ref('realtime-db')
        dbRef.on('value',(snapshot) => {
            const db = snapshot.val()

            const listDate = []
            const listTime = []
            const listPH = []
            const listTDS = []
            const listDO = []
            const listEC = []
            const listORC = []
            const listTEMP = []
            const listLon = []
            const listLat = []
            const listDb = []


            for (let id in db){
                listDate.push({id, ...db[id].date})
                listTime.push({id, ...db[id].time})
                listPH.push({id, ...db[id].ph})
                listTDS.push({id, ...db[id].tds})
                listDO.push({id, ...db[id].do})
                listEC.push({id, ...db[id].ec})
                listORC.push({id, ...db[id].orc})
                listTEMP.push({id, ...db[id].temp})
                listLon.push({id, ...db[id].lon})
                listLat.push({id, ...db[id].lat})
                listDb.push({id, ...db[id]})   
            }

            setDbList(listDb)
            setSensorDate(listDate)
            setSensorTime(listTime)
            setSensorPH(listPH)
            setSensorTDS(listTDS)
            setSensorDO(listDO)
            setSensorEC(listEC)
            setSensorORC(listORC)
            setSensorTEMP(listTEMP)
            setSensorLon(listLon)
            setSensorLat(listLat)

            //console.log(listTDS);
        })
    },[])

    const data = {
        labels: ['1', '2', '3', '4', '5', '6'],
        datasets: [
          {
            label: '# of Votes',
            data: [12, 19, 3, 5, 2, 3],
            fill: false,
            backgroundColor: 'rgb(255, 99, 132)',
            borderColor: 'rgba(255, 99, 132, 0.2)',
          },
        ],
      }

    const options = {
        scales: {
          yAxes: [
            {
              ticks: {
                beginAtZero: true,
              },
            },
          ],
        },
      }



    return (
        <div>
            {
                console.log('sad')
            }
            <Line data={data} options={options} />
        </div>
    );
}

export default GraphPlot;
